import React, { useState, useEffect, useRef } from 'react';

const images = [
  '/logo192.png',
  '/logo512.png',
  '/favicon.ico',
  '/manifest.json' // Using public folder files as placeholders
];

const ImageCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const carouselRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (carouselRef.current) {
      carouselRef.current.style.transform = `translateX(-${currentIndex * 100}%)`;
      carouselRef.current.style.transition = 'transform 0.5s ease-in-out';
    }
  }, [currentIndex]);

  const goToNext = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
  };

  const goToPrev = () => {
    setCurrentIndex((currentIndex - 1 + images.length) % images.length);
  };

  return (
    <div style={{ position: 'relative', overflow: 'hidden', width: '100%', marginTop: '10px', height: '250px' }}>
      <div
        ref={carouselRef}
        style={{
          display: 'flex',
          width: `${images.length * 100}%`,
          height: '250px'
        }}
      >
        {images.map((src, index) => (
          <img
            key={index}
            src={src}
            alt={`carousel-${index}`}
            style={{ width: '25%', flexShrink: 0, height: '250px', objectFit: 'cover' }}
          />
        ))}
      </div>
      <button
        onClick={goToPrev}
        style={{
          position: 'absolute',
          top: '50%',
          left: '10px',
          transform: 'translateY(-50%)',
          backgroundColor: 'rgba(0,0,0,0.5)',
          color: 'white',
          border: 'none',
          borderRadius: '50%',
          width: '30px',
          height: '30px',
          cursor: 'pointer',
          zIndex: 1
        }}
        aria-label="Previous"
      >
        &#8249;
      </button>
      <button
        onClick={goToNext}
        style={{
          position: 'absolute',
          top: '50%',
          right: '10px',
          transform: 'translateY(-50%)',
          backgroundColor: 'rgba(0,0,0,0.5)',
          color: 'white',
          border: 'none',
          borderRadius: '50%',
          width: '30px',
          height: '30px',
          cursor: 'pointer',
          zIndex: 1
        }}
        aria-label="Next"
      >
        &#8250;
      </button>
    </div>
  );
};

export default ImageCarousel;
